sysauto_script.script:
var rightNow = new GlideDateTime();
var nextDay = new GlideDateTime();
nextDay.addDaysLocalTime(1);

var currentDateTasks = new GlideRecord('x_58872_needit_needit_task');
currentDateTasks.addQuery('due_date','>=',rightNow);
currentDateTasks.addQuery('due_date','<=',nextDay);
currentDateTasks.addQuery('state','!=',3);

currentDateTasks.query();

while(currentDateTasks.next()){
	gs.info("Task that will overdue tomorrow = " + currentDateTasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',currentDateTasks,currentDateTasks.number,gs.getUserName());
}
